    //     if (res.data.code === 1000) {
    //       console.log("Bearer", res.data.data);
    //       save(saveData, res.data.data)
    //         .then((res) => {
    //           console.log(res);
    //         })
    //         .catch((e) => {
    //           console.log("Error : ", e);
    //         });
    //     }
    //   })
    //   .catch((e) => {
    //     console.log("Error : ", e);
    //   });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          팀명:
          <input type="text" value={teamName} onChange={handleInputChange} />
        </label>
        <button type="submit">전송</button>
      </form>
    </div>
  );
}

export default App;
