import { useState } from "react";
import { auth } from "./api";
import cryptoJs from "crypto-js";

function App() {
  const [teamName, setTeamName] = useState("");

  const handleInputChange = (e) => {
    setTeamName(e.target.value);
  };

  const generateAuthHash = () => {
    // const message = "test";
    // const secretKey = "NHVzLwHNeIXuNIiXerePIGVj5kzJmJYA";
    // const hmac = crypto.createHmac("sha256", secretKey);
    // hmac.update(message);
    // const authHash = hmac.digest("base64");
    // return authHash;
    const message = teamName;
    const secretKey = "NHVzLwHNeIXuNIiXerePIGVj5kzJmJYA";
    const hmac = cryptoJs.HmacSHA256(message, secretKey);
    const authHash = cryptoJs.enc.Base64.stringify(hmac);
    return authHash;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const authData = {
      message: "test",
      authHash: generateAuthHash(),
    };
    const authRes = auth(authData);
    console.log(authRes);
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          팀명:
          <input type="text" value={teamName} onChange={handleInputChange} />
        </label>
        <button type="submit">전송</button>
      </form>
      {/* <p>{responseMsg}</p> */}
    </div>
  );
}

export default App;
