import React, { useState } from "react";
import crypto from "crypto";

function TeamInputPage() {
  const [teamName, setTeamName] = useState("");
  const [responseMsg, setResponseMsg] = useState("");

  const handleInputChange = (event) => {
    setTeamName(event.target.value);
  };

  const generateAuthHash = () => {
    const message = "test";
    const secretKey = "YOUR_SECRET_KEY"; // 여기에 서버에서 제공받은 시크릿 키를 넣어야 합니다.
    const hmac = crypto.createHmac("sha256", secretKey);
    hmac.update(message);
    const authHash = hmac.digest("base64");
    return authHash;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Step 1: auth API 호출
    const authUrl = "http://118.67.134.151/hackathon/api/auth";
    const authRequest = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        message: "test",
        authHash: generateAuthHash(),
      }),
    };

    try {
      const authResponse = await fetch(authUrl, authRequest);
      const authData = await authResponse.json();

      if (authData.code === 1000) {
        // Step 2: save API 호출
        const saveUrl = "http://118.67.134.151/hackathon/api/save";
        const saveRequest = {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ teamName: teamName }),
        };

        const saveResponse = await fetch(saveUrl, saveRequest);
        const saveData = await saveResponse.json();

        // 성공적인 응답 메시지를 상태에 저장
        setResponseMsg(saveData.msg);
      } else {
        setResponseMsg("인증 실패");
      }
    } catch (error) {
      setResponseMsg("API 호출 오류: " + error.message);
    }
  };

  return (
    <div>
      <h1>팀명 입력 페이지</h1>
      <form onSubmit={handleSubmit}>
        <label>
          팀명:
          <input type="text" value={teamName} onChange={handleInputChange} />
        </label>
        <button type="submit">전송</button>
      </form>
      <p>{responseMsg}</p>
    </div>
  );
}

export default TeamInputPage;
